--
-- 表的结构 `u_user`
--

CREATE TABLE IF NOT EXISTS `u_user` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `u_name` varchar(30) NOT NULL,
  `u_password` varchar(40) NOT NULL,
  `u_email` varchar(30) NOT NULL,
  `u_tel` int(11) NOT NULL,
  `u_area` varchar(30) NOT NULL,
